"use strict";

console.log(1 > 2);
/*false*/

console.log(1 == 2);
/*false*/

var score = 100;
console.log(score == 101);
/*false*/

console.log(2 == 2);
/*true*/

console.log(100 == "100");
/*true*/

console.log(100 === "100");
/*false*/

console.log(1 != 2);
/*true*/

console.log(1 !== 1);
/*false*/

console.log(1 !== "1");
/*true*/